#define AGEING_TIME 300  // 300秒老化时间

table mac_learning_table {
    key = {
        vlan.vid : exact;
        ethernet.srcAddr : exact;
    }
    actions = {
        update_timestamp;
        no_op;
    }
}

table mac_forwarding_table {
    key = {
        vlan.vid : exact;
        ethernet.dstAddr : exact;
    }
    actions = {
        forward_to_port;
        flood_in_vlan;  // VLAN内泛洪
